        return verifier.verifyProof(_pA, _pB, _pC, [config[validationId]]);
    }
}

contracts/Validator/Support/RandomValida.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "hardhat/console.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import {VRFConsumerBaseV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/VRFConsumerBaseV2Plus.sol";
import {VRFV2PlusClient} from "@chainlink/contracts/src/v0.8/vrf/dev/libraries/VRFV2PlusClient.sol";
import "./TwoStepValidator.sol";


contract RandomValidator is TwoStepValidator, VRFConsumerBaseV2Plus {
    // Your subscription ID.
    uint256 immutable s_subscriptionId;

    // The gas lane to use, which specifies the maximum gas price to bump to.
    // For a list of available gas lanes on each network,
    // see https://docs.chain.link/docs/vrf-contracts/#configurations
    bytes32 immutable s_keyHash;

    // Depends on the number of requested values that you want sent to the
    // fulfillRandomWords() function. Storing each word costs about 20,000 gas,
    // so 100,000 is a safe default for this example contract. Test and adjust
    // this limit based on the network that you select, the size of the request,
    // and the processing of the callback request in the fulfillRandomWords()
    // function.
    uint32 constant CALLBACK_GAS_LIMIT = 100000;

    // The default is 3, but you can set this higher.
    uint16 constant REQUEST_CONFIRMATIONS = 3;

    // For this example, retrieve 1 random values in one request.
    // Cannot exceed VRFCoordinatorV2_5.MAX_NUM_WORDS.
    uint32 constant NUM_WORDS = 1;

    event ReturnedRandomness(uint256[] randomWords);

    /**
     * @notice Constructor inherits VRFConsumerBaseV2Plus
     *
     * @param subscriptionId - the subscription ID that this contract uses for funding requests
     * @param vrfCoordinator - coordinator, check https://docs.chain.link/vrf/v2-5/supported-networks
     * @param keyHash - the gas lane to use, which specifies the maximum gas price to bump to
     */
    constructor(
        uint256 subscriptionId,
        address vrfCoordinator,
        bytes32 keyHash
    ) VRFConsumerBaseV2Plus(vrfCoordinator) {
        s_keyHash = keyHash;
        s_subscriptionId = subscriptionId;
    }

    function setConfig(uint256 validationId, uint256 publicHash) public {
        // TODO
    }

    function getConfig(uint256 /* validationId */) external pure returns (string memory) {
        return string.concat("{}");
    }

    /**
     * @notice Requests randomness
     * Assumes the subscription is funded sufficiently; "Words" refers to unit of data in Computer Science
     */
    function preValidation(uint256 validationId, bytes calldata /* preValidationParams */) external returns (bytes32) {
        // Will revert if subscription is not set and funded.
        uint256 requestId = s_vrfCoordinator.requestRandomWords(
            VRFV2PlusClient.RandomWordsRequest({
                keyHash: s_keyHash,
                subId: s_subscriptionId,
                requestConfirmations: REQUEST_CONFIRMATIONS,
                callbackGasLimit: CALLBACK_GAS_LIMIT,
                numWords: NUM_WORDS,
                extraArgs: VRFV2PlusClient._argsToBytes(
                    VRFV2PlusClient.ExtraArgsV1({nativePayment: false})
                )
            })
        );

        bytes32 claimUID = keccak256(abi.encodePacked(validationId, msg.sender));
        validationRequest[bytes32(requestId)] = claimUID;
        claimState[claimUID] = ValidationState.PREVALIDATION;

        // DEBUG
        lastRequestId = bytes32(requestId);

        return claimUID;
    }

    /**
     * @notice Callback function used by VRF Coordinator
     *
     * @param _requestId - id of the request
     * @param randomWords - array of random results from VRF Coordinator
     */
    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] calldata randomWords
    ) internal override {
        bytes32 _requestIdb32 = bytes32(_requestId);
        bytes32 _claim_uid = validationRequest[_requestIdb32];

        if (randomWords[0] % 2 == 1) {
            claimState[_claim_uid] = ValidationState.SUCCESS;
        } else {
            claimState[_claim_uid] = ValidationState.FAIL;
        }

        // emit ReturnedRandomness(randomWords);
    }
}

contracts/Validator/Support/TwoStepValid.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "hardhat/console.sol";
import "../../Challenges/IValidator.sol";
import "./ITwoStepValidator.sol";


abstract contract TwoStepValidator is IValidator, ITwoStepValidator {

    // keccak256(abi.encodePacked(validationId, user account)) -> claimed 
    mapping(bytes32 => ValidationState) public claimState;
    // request id -> keccak256(abi.encodePacked(validationId, user account))
    mapping(bytes32 => bytes32) public validationRequest;

    // Debug info
    bytes32 public lastRequestId;

    function getValidationState(uint256 validationId) public view override returns (string memory) {
        bytes32 claimUID = keccak256(abi.encodePacked(validationId, msg.sender));
        ValidationState state = claimState[claimUID];
        string memory state_str;
        if (state == ValidationState.PREVALIDATION) {
            state_str = "PREVALIDATION";
        } else if (state == ValidationState.SUCCESS) {
            state_str = "SUCCESS";
        } else if (state == ValidationState.FAIL) {
            state_str = "FAIL";
        } else {
            state_str = "NOSTATE";
        }
        return string.concat("{\"state\": \"", state_str, "\"}");
    }

    function validate(uint256 validationId, bytes calldata params) public view override returns (bool) {
        (address[1] memory sender) = abi.decode(params, (address[1]));
        bytes32 claimUID = keccak256(abi.encodePacked(validationId, sender[0]));
        return claimState[claimUID] == ValidationState.SUCCESS;
    }
}

contracts/Validator/ValidatorRegistry.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;
import "hardhat/console.sol";
import "../Challenges/IChallengeManager.sol";
import "../Organization/IOrganizationManager.sol";


contract ValidatorRegistry {
    IChallengeManager public challangeManager;
    IOrganizationManager public organizationManager;
    mapping(bytes32 => address) public validators;

    constructor(address _organizationManagerAddr, address _challangeManagerAddr) {
        challangeManager = IChallengeManager(_challangeManagerAddr);
        organizationManager = IOrganizationManager(_organizationManagerAddr);
    }

    function registerValidator(bytes32 _validatorUID, address _validatorAddress) public {
        validators[_validatorUID] = _validatorAddress;
    }

    function setChallengeValidator(uint256 _challengeId, bytes32 _validatorUID, uint256 _validationId) public {
        uint256 _orgId = challangeManager.getOrganizationId(_challengeId);
        require(organizationManager.isAdmin(_orgId, msg.sender), "Not an admin");

        address _validatorAddress = validators[_validatorUID];
        require(_validatorAddress != address(0), "Invalid _validatorUID, please register the validator.");

        challangeManager.setChallengeValidator(_challengeId, _validatorAddress, _validatorUID, _validationId);
    }
}

