// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "hardhat/console.sol";
import "./OrganizationToken.sol";
import "../Challenges/IChallengeManager.sol";
import {IOrganizationManager} from "./IOrganizationManager.sol";

contract OrganizationManager is IOrganizationManager {
	// Estructuras
	struct Organization {
		uint256 id;
		string name;
		address token;
		string baseURI;
		mapping(address => bool) adminExists;
		address[] admins;
		bool exists;
	}

	// Atributos
	uint256 public orgCount;
	mapping(uint256 => Organization) public organizations;
	mapping(string => bool) public orgNameExists;
	mapping(string => bool) public tokenSymbolExists;

	uint256[] public organizationIds;
	// address public cryptoTrophyToken;

	// Eventos
	event OrganizationCreated(
		uint256 indexed orgId,
		string name,
		address token,
		string baseURI
	);

	event OrganizationAdminAdded(
		uint256 indexed orgId,
		address indexed adminAddress
	);

	// Modificadores
	modifier onlyAdmin(uint256 orgId) {
		require(organizations[orgId].adminExists[msg.sender], "Not an admin");
		_;
	}

	function getTokenOfOrg(uint256 _orgId) external view override returns (address) {
		Organization storage org = organizations[_orgId];
		require(org.exists, "Organization does not exist");
		return org.token;
	}

	/// @notice Obtiene el balance de una organización en su token con los 18 decimales
	function getBalanceOfOrg(uint256 _orgId) external view override returns (uint256) {
		Organization storage org = organizations[_orgId];
		require(org.exists, "Organization does not exist");
		return ERC20(org.token).balanceOf(address(this));
	}

	// @return Balance de un usuario en una organización y el nombre del token
	function getBalanceOfUser(uint256 _orgId, address _user) external view override returns (uint256, string memory) {
		Organization storage org = organizations[_orgId];
		require(org.exists, "Organization does not exist");
		return (ERC20(org.token).balanceOf(_user), ERC20(org.token).symbol());
	}

	function transferTokensTo(uint256 _orgId, address _destAddress, uint256 _amount) external {
		// require(msg.sender == address(challengeManagerAddr), "Only ChallengeManager can call");
		Organization storage org = organizations[_orgId];
		require(org.exists, "Organization does not exist");

		// Validar balance
		uint256 orgBalance = ERC20(org.token).balanceOf(address(this));
		require(orgBalance >= _amount, "Not enough tokens in OrgManager");

		// Hacer transferencia
		ERC20(org.token).transfer(_destAddress, _amount);
	}

	// Funciones

	/// @notice Crea una nueva organización
	function createOrganization(
		string memory _name,
		string memory _symbol,
		uint256 _initialSupply,
		uint256 _initialEthBacking,
		address[] memory _admins,
		string memory _baseURI
	) public payable returns (uint256) {
		require(msg.value >= _initialEthBacking, "Insufficient ETH backing");
		require(!orgNameExists[_name], "Organization name already exists");
		require(!tokenSymbolExists[_symbol], "Token symbol already exists");
		orgNameExists[_name] = true;
		tokenSymbolExists[_symbol] = true;

		// Crear nuevo token de la organización y asignar tokens al contrato
		OrganizationToken token = new OrganizationToken(
			_name,
			_symbol,
			_initialSupply,
			address(this)
		);
		require(address(token) != address(0), "Failed to create token");

		// Send ETH backing to the token contract if provided
		if (_initialEthBacking > 0) {
			(bool success, ) = address(token).call{value: _initialEthBacking}("");
			require(success, "Failed to send ETH to token contract");
		}

		// Crear y registrar organización
		uint256 orgId = orgCount++;
		Organization storage org = organizations[orgId];
		org.id = orgId;
		org.name = _name;
		org.token = address(token);
		org.exists = true;
		org.baseURI = _baseURI;

		// Por default, el creador de la organización es admin
		_addAdmin(orgId, msg.sender);

		for (uint256 i = 0; i < _admins.length; i++) {
			_addAdmin(orgId, _admins[i]);
		}

		organizationIds.push(orgId);
		emit OrganizationCreated(orgId, _name, address(token), _baseURI);
		return orgId;
	}

	function _addAdmin(uint256 _orgId, address _admin) internal {
		if (!organizations[_orgId].adminExists[_admin]) {
			organizations[_orgId].adminExists[_admin] = true;
			organizations[_orgId].admins.push(_admin);
			emit OrganizationAdminAdded(_orgId, _admin);
		}
	}

	/// @notice Agrega un administrador a una organización
	function addAdmin(uint256 _orgId, address _admin) public onlyAdmin(_orgId) {
		_addAdmin(_orgId, _admin);
	}

	// Implementación de las funciones de la interfaz

	/// @notice Verifica si una dirección es administrador de una organización
	function isAdmin(uint256 _orgId, address _user) external view override returns (bool) {
		Organization storage org = organizations[_orgId];
		require(org.exists, "Organization does not exist");
		return org.adminExists[_user];
	}

	/// @notice Lista todas las organizaciones
	function listOrganizations() public view returns (uint256[] memory) {
		return organizationIds;
	}

	// @notice Returns details ONLY for organizations where msg.sender is an admin.
	// @return orgIds Array of IDs for organizations administered by the sender.
	// @return names Array of names for those organizations.
	// @return tokenSymbols Array of token symbols for those organizations.
	// @return tokenAddresses Array of token contract addresses for those organizations.
	// @return adminCounts Array of total admin counts for those organizations.
	function listAdministratedOrganizations() public view returns (
		uint256[] memory orgIds,
		string[] memory names,
		string[] memory tokenSymbols,
		address[] memory tokenAddresses,
		uint256[] memory adminCounts
	) {
		uint256 adminOrgCount = 0;
		for (uint256 i = 0; i < orgCount; i++) {
			if (organizations[i].exists && organizations[i].adminExists[msg.sender]) {
				adminOrgCount++;
			}
		}
		orgIds = new uint256[](adminOrgCount);
		names = new string[](adminOrgCount);
		tokenSymbols = new string[](adminOrgCount);
		tokenAddresses = new address[](adminOrgCount);
		adminCounts = new uint256[](adminOrgCount);

		uint256 resultIndex = 0;
		for (uint256 i = 0; i < orgCount; i++) {
			if (organizations[i].exists && organizations[i].adminExists[msg.sender]) {
				Organization storage org = organizations[i];

				orgIds[resultIndex] = org.id;
				names[resultIndex] = org.name;
				if (org.token != address(0)) {
					tokenSymbols[resultIndex] = ERC20(org.token).symbol();
				} else {
					tokenSymbols[resultIndex] = "";
				}
				tokenAddresses[resultIndex] = org.token;
				adminCounts[resultIndex] = org.admins.length;
				resultIndex++;
			}
		}
	}


	// @notice Devuelve las organizaciones a las que el sender pertenece como admin o usuario, incluyendo nombre y dirección del token
	function listOrganizationsWithDetails() public view returns (
		uint256[] memory orgIds,
		string[] memory names,
		string[] memory tokenSymbols,
		address[] memory tokenAddresses,
		uint256[] memory adminCounts
	) {
		orgIds = new uint256[](orgCount);
		names = new string[](orgCount);
		tokenSymbols = new string[](orgCount);
		tokenAddresses = new address[](orgCount);
		adminCounts = new uint256[](orgCount);

		for (uint256 i = 0; i < orgCount; i++) {
			Organization storage org = organizations[i];
			orgIds[i] = org.id;
			names[i] = org.name;
			tokenSymbols[i] = ERC20(org.token).symbol();
			tokenAddresses[i] = org.token;
			adminCounts[i] = org.admins.length;
		}
	}

	/// @notice Obtiene los detalles de una organización por ID
	function getOrganizationDetails(uint256 _orgId) public view returns (
		uint256 orgId,
		string memory name,
		address token,
		address[] memory admins,
		bool userIsAdmin,
		string memory baseURI
		) {
		Organization storage org = organizations[_orgId];
		require(org.exists, "Organization does not exist");

		orgId = org.id;
		name = org.name;
		token = org.token;
		admins = org.admins;
		userIsAdmin = org.adminExists[msg.sender];
		baseURI = org.baseURI;
	}
}

contracts/Organization/OrganizationToken.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract OrganizationToken is ERC20, Ownable, ERC20Burnable, ReentrancyGuard {
    bool public redemptionEnabled;

    event RedemptionEnabled(bool enabled);
    event TokensRedeemed(address indexed user, uint256 tokenAmount, uint256 ethAmount);

    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply,
        address initialRecipient
    ) ERC20(name, symbol) Ownable(msg.sender) {
        _mint(initialRecipient, initialSupply * (10 ** decimals()));
        redemptionEnabled = false;
    }

    // Allow contract to receive ETH
    receive() external payable onlyOwner {
        redemptionEnabled = true;
    }

    function getCurrentExchangeRate() public view returns (uint256) {
        require(totalSupply() > 0, "No tokens in circulation");
        require(address(this).balance > 0, "No ETH available for redemption");
        return totalSupply() / address(this).balance;
    }

    function redeemTokensForEth(uint256 tokenAmount) external nonReentrant {
        require(redemptionEnabled, "Redemption not enabled");
        require(balanceOf(msg.sender) >= tokenAmount, "Insufficient token balance");
        require(tokenAmount > 0, "Amount must be greater than 0");
        require(address(this).balance > 0, "No ETH available for redemption");

        uint256 currentRate = getCurrentExchangeRate();
        uint256 ethAmount = tokenAmount / currentRate;
        require(address(this).balance >= ethAmount, "Insufficient ETH in contract");

        // Burn tokens first
        _burn(msg.sender, tokenAmount);

        // Transfer ETH to user
        (bool success, ) = msg.sender.call{value: ethAmount}("");
        require(success, "ETH transfer failed");

        emit TokensRedeemed(msg.sender, tokenAmount, ethAmount);
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }
}

contracts/Prizes/Prizes.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "../Organization/IOrganizationManager.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

// NFT contract for prizes
contract PrizeNFT is ERC721URIStorage, Ownable {
    uint256 private _nextTokenId;

    string private _baseTokenURI;

    constructor(string memory name, string memory symbol, string memory baseTokenURI) 
        ERC721(name, symbol) 
        Ownable(msg.sender)
    {
        _baseTokenURI = baseTokenURI;
    }

    function mint(address to) external onlyOwner returns (uint256) {
        uint256 tokenId = _nextTokenId++;
        _safeMint(to, tokenId);
        return tokenId;
    }

    function _baseURI() internal view override returns (string memory) {
        return _baseTokenURI;
    }
}

contract Prizes {
    // -------------------------------------------------------------------------
    // ESTRUCTURAS Y ALMACENAMIENTO
    // -------------------------------------------------------------------------

    struct Prize {
        // Como se pide: name, description, price, stock
        string name;
        string description;
        uint256 price;
        uint256 stock;
        uint256 orgId;
        address nftContract; // Address of the deployed NFT contract for this prize
        string baseURI; // Image cid in Filebase
    }

    mapping(uint256 => Prize) private prizes; // prizeId => Prize
    uint256 private nextPrizeId;

    // prizesByOrg[orgId] => array dinámico de premios de esa organización
    mapping(uint256 => uint256[]) private prizeIdsByOrg;

    // Referencia al OrganizationManager para verificar admins y miembros,
    // y para obtener el token de la org.
    IOrganizationManager public orgManager;

    // EVents
    event PrizeCreated(
        uint256 indexed prizeId,
        uint256 indexed orgId,
        string name,
        string description,
        uint256 price,
        uint256 stock,
        address nftContract,
        string baseURI
    );

    event PrizeClaimed(
        uint256 indexed prizeId,
        uint256 indexed orgId,
        uint256 amount,
        address claimer,
        uint256 cost,
        uint256[] nftIds
    );

    // -------------------------------------------------------------------------
    // CONSTRUCTOR
    // -------------------------------------------------------------------------

    constructor(IOrganizationManager _orgManager) {
        orgManager = _orgManager;
    }

    // -------------------------------------------------------------------------
    // MODIFICADORES
    // -------------------------------------------------------------------------

    /// @dev Verifica si msg.sender es admin en la org correspondiente
    modifier onlyOrgAdmin(uint256 orgId) {
        require(
            orgManager.isAdmin(orgId, msg.sender),
            "Prizes: caller is not an admin of this org"
        );
        _;
    }

    // -------------------------------------------------------------------------
    // FUNCIONES PRINCIPALES
    // -------------------------------------------------------------------------

    /// @notice Step 1: Create NFT contract for a prize
    /// @param name Name of the prize (and NFT)
    /// @param baseURI Image cid in Filebase
    /// @return NFT contract address
    function _createNFTContract(string memory name, uint256 prizeIndex, string memory baseURI)
    internal
    returns (address)
    {
        string memory symbol = string(abi.encodePacked("PRIZE", Strings.toString(prizeIndex)));

        PrizeNFT nftContract = new PrizeNFT(name, symbol, baseURI);
        return address(nftContract);
    }

    /// @notice Step 2: Store prize data and link to organization
    /// @param prizeId ID for the new prize
    /// @param orgId Organization ID
    /// @param name Prize name
    /// @param description Prize description
    /// @param price Prize price in org tokens
    /// @param stock Initial stock amount
    /// @param nftContract Address of the NFT contract
    /// @param baseURI Image cid in Filebase
    function _storePrize(
        uint256 prizeId,
        uint256 orgId,
        string memory name,
        string memory description,
        uint256 price,
        uint256 stock,
        address nftContract,
        string memory baseURI
    )
    internal
    {
        Prize memory newPrize = Prize({
            name: name,
            description: description,
            price: price,
            stock: stock,
            orgId: orgId,
            nftContract: nftContract,
            baseURI: baseURI
        });

        prizes[prizeId] = newPrize;
        prizeIdsByOrg[orgId].push(prizeId);

        emit PrizeCreated(
            prizeId,
            orgId,
            name,
            description,
            price,
            stock,
            nftContract,
            baseURI
        );
    }

    /// @notice Crea un nuevo premio para la organización `orgId`.
    /// @param orgId ID de la organización
    /// @param name Nombre del premio
    /// @param description Descripción del premio
    /// @param price Precio (en tokens de la organización) para reclamar 1 unidad
    /// @param stock Cantidad inicial de unidades disponibles de este premio
    /// @param baseURI Image cid in Filebase
    function createPrize(
        uint256 orgId,
        string calldata name,
        string calldata description,
        uint256 price,
        uint256 stock,
        string calldata baseURI
    )
    external
    onlyOrgAdmin(orgId)
    {
        uint256 prizeId = nextPrizeId++;

        // Step 1: Create NFT contract
        address nftContract = _createNFTContract(name, prizeId, baseURI);

        // Step 2: Store prize data
        _storePrize(
            prizeId,
            orgId,
            name,
            description,
            price,
            stock,
            nftContract,
            baseURI
        );
    }

    function getPrize(uint256 prizeId) external view returns (Prize memory) {
        return prizes[prizeId];
    }

    /// @notice Lista todos los premios de una organización, incluyendo su ID e información.
    /// @dev Retorna un array de structs `Prize` pero en Solidity 0.8.x se puede usar
    ///     sin problema mientras las strings sean memoria.
    ///     También retornamos el `prizeId` como índice para que el front lo identifique.
    /// @param orgId ID de la organización
    /// @return ids Lista de IDs (índices) de cada premio
    /// @return names Lista de nombres
    /// @return descriptions Lista de descripciones
    /// @return prices Lista de precios
    /// @return stocks Lista de stocks
    /// @return nftContracts Lista de direcciones de contratos NFT
    /// @return baseURI Lista de cids de imágenes de premios
    function listPrizes(uint256 orgId)
    external
    view
    returns (
        uint256[] memory ids,
        string[] memory names,
        string[] memory descriptions,
        uint256[] memory prices,
        uint256[] memory stocks,
        address[] memory nftContracts,
        string[] memory baseURI
    )
    {
        uint256[] memory prizeIds = prizeIdsByOrg[orgId];
        uint256 length = prizeIds.length;

        ids = new uint256[](length);
        names = new string[](length);
        descriptions = new string[](length);
        prices = new uint256[](length);
        stocks = new uint256[](length);
        nftContracts = new address[](length);
        baseURI = new string[](length);

        for (uint256 i = 0; i < length; i++) {
            uint256 prizeId = prizeIds[i];
            Prize storage p = prizes[prizeId];

            ids[i] = prizeId;
            names[i] = p.name;
            descriptions[i] = p.description;
            prices[i] = p.price;
            stocks[i] = p.stock;
            nftContracts[i] = p.nftContract;
            baseURI[i] = p.baseURI;
        }
    }

    /// @notice Reclama `amount` unidades de un premio (paga en tokens de la org).
    /// @dev El usuario debe haber aprobado previamente a este contrato (`Prizes`)
    ///      para gastar `amount * price` tokens de la organización.
    /// @param orgId ID de la organización
    /// @param prizeId ID del premio 
    /// @param amount Cantidad de unidades que se reclaman
    function claimPrize(uint256 orgId, uint256 prizeId, uint256 amount)
    external
    {
        require(amount > 0, "Prizes: amount must be > 0");

        Prize storage p = prizes[prizeId];
        require(p.orgId == orgId, "Prizes: prize not from this org");
        require(p.stock >= amount, "Prizes: not enough stock");

        // Costo total en tokens
        uint256 cost = p.price * amount;

        // Obtener la dirección del token de la organización
        address tokenAddress = orgManager.getTokenOfOrg(orgId);
        require(tokenAddress != address(0), "Prizes: invalid token for org");

        // Transferir tokens desde el usuario a este contrato (requiere approve)
        IERC20(tokenAddress).transferFrom(msg.sender, address(this), cost);

        // Mint NFTs to the claimer
        uint256[] memory nftIds = new uint256[](amount);
        for (uint256 i = 0; i < amount; i++) {
            nftIds[i] = PrizeNFT(p.nftContract).mint(msg.sender);
        }

        // Disminuir el stock
        p.stock -= amount;

        emit PrizeClaimed(prizeId, orgId, amount, msg.sender, cost, nftIds);

        // Opcionalmente: "quemar" tokens o lo que se desee hacer con esos tokens
        // Si se pueden quemar:
        // CompanyToken(tokenAddress).burn(cost);
        // O transferir a una dirección sin clave:
        // IERC20(tokenAddress).transfer(address(0xdead), cost);
    }
}

