// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "hardhat/console.sol";
import {FunctionsClient} from "@chainlink/contracts/src/v0.8/functions/v1_0_0/FunctionsClient.sol";
import {ConfirmedOwner} from "@chainlink/contracts/src/v0.8/shared/access/ConfirmedOwner.sol";
import {FunctionsRequest} from "@chainlink/contracts/src/v0.8/functions/v1_0_0/libraries/FunctionsRequest.sol";

import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import "./TwoStepValidator.sol";
import "../../Challenges/IValidator.sol";


struct OffChainApiConfig {
    bool exists;
    string apiUrl;
    string dataPath;
    address callback;
}


contract OffChainApiValidator is TwoStepValidator, FunctionsClient, ConfirmedOwner  {
    using FunctionsRequest for FunctionsRequest.Request;

    error UnexpectedRequestID(bytes32 requestId);

    // Chainlink variables
    address public router;
    bytes32 public donId;
    uint64 public subscriptionId;
    string public source;
    uint32 public constant MAX_CALLBACK_GAS = 70_000;

    // validationId -> validatorConfig
    mapping(uint256 => OffChainApiConfig) public config;

    bytes32 validatorUID;

    event OffChainApiValidatorCalled(uint256 validationId, address indexed claimer, bytes32 indexed requestId);
    event OffChainApiValidatorFulfilled(bytes32 indexed requestId, bytes32 response);
    event OffChainApiValidatorError(bytes32 indexed requestId, bytes errorData);

    constructor(
        address _router
        , bytes32 _donId
        , string memory _source
        , uint64 _subscriptionId
        , bytes32 _validatorUID
    ) FunctionsClient(_router) ConfirmedOwner(msg.sender) {
        router = _router;
        donId = _donId;
        source = _source;
        subscriptionId = _subscriptionId;
        validatorUID = _validatorUID;
    }

	function setConfigFromParams(uint256 validationId, bytes calldata params) public {
		(
			string memory _apiUrl,
			string memory _dataPath,
			address _callback
		) = abi.decode(params, (string, string, address));

        config[validationId] = OffChainApiConfig(true, _apiUrl, _dataPath, _callback);
	}

    function setConfig(uint256 _validationId, string calldata _apiUrl, string calldata _dataPath, address _callback) public {
        config[_validationId] = OffChainApiConfig(true, _apiUrl, _dataPath, _callback);
    }

    function getConfig(uint256 _validationId) external view returns (string memory) {
        OffChainApiConfig storage _validatorConfig = config[_validationId];
		require(_validatorConfig.exists, "Error. Invalid configuration. No API configured for validationId.");
        return string(abi.encodePacked('{"apiUrl":"', _validatorConfig.apiUrl, '"}'));
    }

    function preValidation(uint256 _validationId, bytes calldata /* preValidationParams */) external payable returns (bytes32) {
        OffChainApiConfig storage _validatorConfir = config[_validationId];
		require(_validatorConfir.exists, "Error. Invalid configuration. No API configured for validationId.");

        // Specify the url to call
        string memory url = string(
            abi.encodePacked(
                _validatorConfir.apiUrl,
                Strings.toString(_validationId),
                "/",
                Strings.toString(uint256(keccak256(abi.encodePacked(msg.sender))))
            )
        );
        string[] memory args = new string[](2);
        args[0] = url;
        args[1] = _validatorConfir.dataPath;

        // Sends the request
        FunctionsRequest.Request memory req;
        req.initializeRequestForInlineJavaScript(source);
        req.setArgs(args);
        bytes32 requestId = _sendRequest(
            req.encodeCBOR(),
            subscriptionId,
            MAX_CALLBACK_GAS,
            donId
        );
        console.log(uint256(requestId));

        // Store the request to trace the fulfill
        bytes32 _claimUID = keccak256(abi.encodePacked(_validationId, msg.sender));
        validationRequest[requestId] = _claimUID;
        claims[_claimUID] = Claim(_validationId, msg.sender, ValidationState.PREVALIDATION);

        emit OffChainApiValidatorCalled(_validationId, msg.sender, requestId);

        return requestId;
    }

    // Callback function. 
    // NOTE: This function is protected by FunctionsClient.
    function fulfillRequest(
        bytes32 requestId,
        bytes memory response,
        bytes memory err
    ) internal override {
        bytes32 _claimUID = validationRequest[requestId];
        require(_claimUID != bytes32(0), "Invalid request ID");
        Claim storage _claim = claims[_claimUID];

        console.log("fulfillRequest validationId", _claim.validationId);
        console.log("fulfillRequest state", err.length);

        if (err.length > 0) {
            // Log the error
            emit OffChainApiValidatorError(requestId, err);
            console.log("Error");

            // Handle error case
            _claim.state = ValidationState.FAIL;
        } else {
            bytes32 _response = bytes32(response);
            emit OffChainApiValidatorFulfilled(requestId, _response);
            console.log("No error");

            if (_response == bytes32(0)) {
                console.log("SUCCESS");
                _claim.state = ValidationState.SUCCESS;
                // Optional: Log the response
                OffChainApiConfig storage _validatorConfig = config[_claim.validationId];
                if (_validatorConfig.callback != address(0)) {
                    IValidatorCallback _callbackContract = IValidatorCallback(_validatorConfig.callback);
                    _callbackContract.validatorClaimCallback(validatorUID, _claim.validationId, _claim.claimer);
                }
            } else {
                console.log("FAIL");
                _claim.state = ValidationState.FAIL;
            }
        }
    }
}

contracts/Validator/Support/OffChainVali.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "hardhat/console.sol";
import {Chainlink, ChainlinkClient} from "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import {ConfirmedOwner} from "@chainlink/contracts/src/v0.8/shared/access/ConfirmedOwner.sol";
import {LinkTokenInterface} from "@chainlink/contracts/src/v0.8/shared/interfaces/LinkTokenInterface.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import "./TwoStepValidator.sol";


struct OffChainConfig {
    bool exists;
    string apiUrl;
    string dataPath;
}


contract OffChainValidator is TwoStepValidator, ChainlinkClient, ConfirmedOwner  {
    using Chainlink for Chainlink.Request;

    // Chainlink variables
    address public oracle;
    bytes32 public jobId;
    address public link;
    uint256 private fee;

    // validationId -> validatorConfig
    mapping(uint256 => OffChainConfig) public config;

    event OffChainRequestSent(uint256 validationId, address indexed claimer, bytes32 indexed requestId);

    constructor(address _oracle, address _link) ConfirmedOwner(msg.sender) {
        _setChainlinkToken(_link);
        _setChainlinkOracle(_oracle);
        oracle = _oracle;
        jobId = "c1c5e92880894eb6b27d3cae19670aa3";
        link = _link;
        fee = (1 * LINK_DIVISIBILITY) / 10;
    }

	function setConfigFromParams(uint256 _validationId, bytes calldata _params) public {
		(
			string memory _apiUrl,
			string memory _dataPath
		) = abi.decode(_params, (string, string));

        config[_validationId] = OffChainConfig(true, _apiUrl, _dataPath);
	}

    function setConfig(uint256 _validationId, string calldata apiUrl, string calldata dataPath) public {
        config[_validationId] = OffChainConfig(true, apiUrl, dataPath);
    }

    function getConfig(uint256 _validationId) external view returns (string memory) {
        OffChainConfig memory paramsStruct = config[_validationId];
		require(paramsStruct.exists, "Error. Invalid configuration. No API configured for validationId.");
        return string.concat("{\"apiUrl\": \"", paramsStruct.apiUrl, "\"}");
    }

    function preValidation(uint256 _validationId, bytes calldata /* preValidationParams */) external payable returns (bytes32) {
        OffChainConfig memory paramsStruct = config[_validationId];
		require(paramsStruct.exists, "Error. Invalid configuration. No API configured for validationId.");

        Chainlink.Request memory request = _buildChainlinkRequest(
            jobId,
            address(this),
            this.fulfill.selector
        ); 

        // Specify the url to call
        string memory url = string(
            abi.encodePacked(
                paramsStruct.apiUrl,
                Strings.toString(_validationId)
            )
        );
        request._add("get", url);

        // Specify the path to find the desired data in the API response
        request._add("path", paramsStruct.dataPath);

        // Sends the request
        bytes32 requestId = _sendChainlinkRequest(request, fee);

        bytes32 claimUID = keccak256(abi.encodePacked(_validationId, msg.sender));
        validationRequest[requestId] = claimUID;
        claims[claimUID] = Claim(_validationId, msg.sender, ValidationState.PREVALIDATION);

        emit OffChainRequestSent(_validationId, msg.sender, requestId);

        return requestId;
    }

    // Callback function
    function fulfill(
        bytes32 _requestId,
        bool _completed
    ) public recordChainlinkFulfillment(_requestId) {
        if (_completed) {
            claims[validationRequest[_requestId]].state = ValidationState.SUCCESS;
        } else {
            claims[validationRequest[_requestId]].state = ValidationState.FAIL;
        }
    }
}

