    Groth16Verifier immutable verifier;

    mapping(uint256 => uint256) public config;

    // Constructor: Called once on contract deployment
    // Check packages/hardhat/deploy/00_deploy_challenge.ts
    constructor(address _groth16Addr) {
        groth16Addr = _groth16Addr;
        verifier = Groth16Verifier(_groth16Addr);
    }

	function setConfigFromParams(uint256 validationId, bytes calldata params) public {
		( uint256 _publicHash ) = abi.decode(params, (uint256));
        config[validationId] = _publicHash;
	}

    function setConfig(uint256 validationId, uint256 publicHash) public {
        config[validationId] = publicHash;
    }

    function getConfig(uint256 validationId) public view returns (string memory) {
        return string.concat("{\"public_hash\": \"", Strings.toString(config[validationId]), "\"}");
    }

    function validate(uint256 validationId, bytes calldata params) public view override returns (bool) {
        // Decode parameters specific to ValidatorA
        (uint[2] memory _pA, uint[2][2] memory _pB, uint[2] memory _pC) = abi.decode(
            params, (uint[2], uint[2][2], uint[2])
        );
        // console.log("Calling validator from %s", msg.sender);
        // console.log("_pA %i %i", _pA[0], _pA[1]);
        // console.log("_pB0 %i %i", _pB[0][0], _pB[0][1]);
        // console.log("_pB1 %i %i", _pB[1][0], _pB[1][1]);
        // console.log("_pC %i %i", _pC[0], _pC[1]);
        // console.log("_pubSignals %i", config[validationId]);

        return verifier.verifyProof(_pA, _pB, _pC, [config[validationId]]);
    }
}

contracts/Validator/Support/RandomValida.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "hardhat/console.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import {VRFConsumerBaseV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/VRFConsumerBaseV2Plus.sol";
import {VRFV2PlusClient} from "@chainlink/contracts/src/v0.8/vrf/dev/libraries/VRFV2PlusClient.sol";
import "./TwoStepValidator.sol";


struct RandomValidatorConfig {
    bool exists;
    // Set the probability in basis points (1% = 100; 0.01% = 1)
    uint256 successProbability;
    address callback;
}


contract RandomValidator is TwoStepValidator, VRFConsumerBaseV2Plus {
    // Your subscription ID.
    uint256 immutable s_subscriptionId;

    // The gas lane to use, which specifies the maximum gas price to bump to.
    // For a list of available gas lanes on each network,
    // see https://docs.chain.link/docs/vrf-contracts/#configurations
    bytes32 immutable s_keyHash;

    // Depends on the number of requested values that you want sent to the
    // fulfillRandomWords() function. Storing each word costs about 20,000 gas,
    // so 100,000 is a safe default for this example contract. Test and adjust
    // this limit based on the network that you select, the size of the request,
    // and the processing of the callback request in the fulfillRandomWords()
    // function.
    uint32 constant CALLBACK_GAS_LIMIT = 200000;

    // The default is 3, but you can set this higher.
    uint16 constant REQUEST_CONFIRMATIONS = 3;

    // For this example, retrieve 1 random values in one request.
    // Cannot exceed VRFCoordinatorV2_5.MAX_NUM_WORDS.
    uint32 constant NUM_WORDS = 1;

    // validationId -> validatorConfig
    mapping(uint256 => RandomValidatorConfig) public configs;

    bytes32 validatorUID;

    event RandomValidatorCalled(uint256 validationId, address indexed claimer, bytes32 indexed requestId);

    /**
     * @notice Constructor inherits VRFConsumerBaseV2Plus
     *
     * @param _subscriptionId - the subscription ID that this contract uses for funding requests
     * @param _vrfCoordinator - coordinator, check https://docs.chain.link/vrf/v2-5/supported-networks
     * @param _keyHash - the gas lane to use, which specifies the maximum gas price to bump to
     * @param _validatorUID - Unique identifier for this validator
     */
    constructor(
        uint256 _subscriptionId
        , address _vrfCoordinator
        , bytes32 _keyHash
        , bytes32 _validatorUID
    ) VRFConsumerBaseV2Plus(_vrfCoordinator) {
        s_keyHash = _keyHash;
        s_subscriptionId = _subscriptionId;
        validatorUID = _validatorUID;
    }

	function setConfigFromParams(uint256 _validationId, bytes calldata _params) public {
		( uint256 _successProbability, address _callback ) = abi.decode(_params, (uint256, address));
        require(_successProbability <= 10000, "Probability must be <= 10000 (100%)");
        configs[_validationId] = RandomValidatorConfig(true, _successProbability, _callback);
	}

    function setConfig(uint256 _validationId, uint256 _successProbability, address _callback) public {
        require(_successProbability <= 10000, "Probability must be <= 10000 (100%)");
        configs[_validationId] = RandomValidatorConfig(true, _successProbability, _callback);
    }

    function getConfig(uint256 _validationId) external view returns (string memory) {
        RandomValidatorConfig storage _validatorConfig = configs[_validationId];
		require(_validatorConfig.exists, "Error. Invalid configuration. Nothing configured for validationId.");
        return string(abi.encodePacked('{"successProbability":"', _validatorConfig.successProbability, '"}'));
    }

    /**
     * @notice Requests randomness
     * Assumes the subscription is funded sufficiently; "Words" refers to unit of data in Computer Science
     */
    function preValidation(uint256 _validationId, bytes calldata /* preValidationParams */) external returns (bytes32) {
        // Will revert if subscription is not set and funded.
        uint256 requestId = s_vrfCoordinator.requestRandomWords(
            VRFV2PlusClient.RandomWordsRequest({
                keyHash: s_keyHash,
                subId: s_subscriptionId,
                requestConfirmations: REQUEST_CONFIRMATIONS,
                callbackGasLimit: CALLBACK_GAS_LIMIT,
                numWords: NUM_WORDS,
                extraArgs: VRFV2PlusClient._argsToBytes(
                    VRFV2PlusClient.ExtraArgsV1({nativePayment: false})
                )
            })
        );

        bytes32 claimUID = keccak256(abi.encodePacked(_validationId, msg.sender));
        validationRequest[bytes32(requestId)] = claimUID;
        claims[claimUID] = Claim(_validationId, msg.sender, ValidationState.PREVALIDATION);

        emit RandomValidatorCalled(_validationId, msg.sender, bytes32(requestId));

        return claimUID;
    }

    /**
     * @notice Callback function used by VRF Coordinator
     *
     * @param _requestId - id of the request
     * @param randomWords - array of random results from VRF Coordinator
     */
    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] calldata randomWords
    ) internal override {
        bytes32 _claimUID = validationRequest[bytes32(_requestId)];
        require(_claimUID != bytes32(0), "Invalid request ID");
        Claim storage _claim = claims[_claimUID];

        RandomValidatorConfig storage _validatorConfig = configs[_claim.validationId];

        if (randomWords[0] % 10_000 < _validatorConfig.successProbability) {
            _claim.state = ValidationState.SUCCESS;
            if (_validatorConfig.callback != address(0)) {
                IValidatorCallback _callbackContract = IValidatorCallback(_validatorConfig.callback);
                _callbackContract.validatorClaimCallback(validatorUID, _claim.validationId, _claim.claimer);
            }
        } else {
            _claim.state = ValidationState.FAIL;
        }
    }
}

contracts/Validator/Support/SecretValida.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.7.0 <0.9.0;

import "hardhat/console.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./Groth16Verifier.sol";
import "../../Challenges/IValidator.sol";

contract SecretValidator is IValidator {
	string public description = "ZKP validator!";
	address public immutable groth16Addr;
	Groth16Verifier immutable verifier;

	mapping(address => uint256) public nonces; // Tracks user-specific nonces

	mapping(uint256 => mapping(uint256 => bool)) public config;
	// Array to store valid hashes for each validation ID (TODO: remove this, is just for debugging)
	mapping(uint256 => uint256[]) public validHashes;

	// Constructor: Called once on contract deployment
	// Check packages/hardhat/deploy/00_deploy_challenge.ts
	constructor(address _groth16Addr) {
		groth16Addr = _groth16Addr;
		verifier = Groth16Verifier(_groth16Addr);
	}

	function setConfigFromParams(uint256 _validationId, bytes calldata _params) public {
		( uint256 length, uint256[] memory _hashes) = abi.decode(_params, (uint256, uint256[]));
		for (uint256 i = 0; i < length; i++) {
			uint256 _publicHash = _hashes[i];
			config[_validationId][_publicHash] = true;
			validHashes[_validationId].push(_publicHash);
		}
	}

	/// @notice Add a list of hashes to the validator
	/// @param _hashes The list of hashes to add
	/// @dev This function is only callable by the owner of the validator
	function setConfig(uint256 validationId, uint256[] calldata _hashes) public {
		for (uint256 i = 0; i < _hashes.length; i++) {
			uint256 _publicHash = _hashes[i];
			config[validationId][_publicHash] = true;
			validHashes[validationId].push(_publicHash);
		}
	}

	function getConfig(
		uint256 validationId
	) public pure returns (string memory) {
		return Strings.toString(validationId);
	}

	function decodeParams(
		bytes calldata params
	)
		public
		pure
		returns (
			uint[2] memory _pA,
			uint[2][2] memory _pB,
			uint[2] memory _pC,
			uint256 publicHash
		)
	{
		(_pA, _pB, _pC, publicHash) = abi.decode(
			params,
			(uint[2], uint[2][2], uint[2], uint256)
		);
	}

	function getPublicSignals(
		uint256 publicHash
	) public view returns (uint256, uint256, uint256) {
		return (uint256(uint160(msg.sender)), nonces[msg.sender], publicHash);
	}

	function getAddressAsField(address sender) public pure returns (uint256) {
		return uint256(uint160(sender));
	}

	function validate(
		uint256 validationId,
		bytes calldata params
	) public view override returns (bool) {
		(
			uint[2] memory _pA,
			uint[2][2] memory _pB,
			uint[2] memory _pC,
			uint256 publicHash
		) = abi.decode(params, (uint[2], uint[2][2], uint[2], uint256));

		require(config[validationId][publicHash], "Invalid hash");

		require(
			verifier.verifyProof(_pA, _pB, _pC, [publicHash]),
			"Invalid proof"
		);
		return true;
	}
}

